import { useAuth } from "../context/AuthContext";
import React, { useState, useEffect } from 'react'
import { Link,useParams } from 'react-router-dom'
import {collection, getDocs, getDoc, deleteDoc, doc, updateDoc} from 'firebase/firestore'
import { db } from "../firebase";
import { info } from "autoprefixer";

export function Home(){
    const {user, logout, loading} = useAuth();
    const handleLogout = async () => {
        await logout();
    }

    const [ description, setDescription ] = useState('') //valor x defecto
    const [stock, setStock] = useState(0) //valor x defecto

    const {id} = useParams()
    const update = async (e) => { //e es un evento
        e.preventDefault() //para evitar comportamiento por defecto
        const product= doc(db, "Persona", id) //traemos todos los datos a product
        const data= {description: description, stock: stock} //obtenemos data
        await updateDoc(product, data) //la data son los datos actualizados, updateDoc es de firestore, para actualizar los datos
    }
    const getProductById = async (id) => {
        const product = await getDoc( doc(db,"products",id) )
        if(product.exists()){
            setDescription(product.data().description)
            setStock(product.data().stock)
        }else{
            console.log('el producto no existe')
        }
    }
    //const mostrarDatos = async () => { //e es un evento
        //e.preventDefault() //para evitar comportamiento por defecto
        //const product= doc(db, "Persona", user.email) //traemos todos los datos a product
        //const data= {description: description, stock: stock} //obtenemos data
       // const userRef = doc(db,`Persona/${user.email}`)
       // const snapshot = await getDoc(userRef)
        //console.log(snapshot.data().user.clave)
        //await updateDoc(userRef, data) //la data son los datos actualizados, updateDoc es de firestore, para actualizar los datos
    //}
    //useEffect(()=>{
    //    mostrarDatos();
    //})
    useEffect( () => {
        getProductById(id)
        // eslint-disable-next-time
    }, [])    
    
    if(loading) return <h1>loading</h1>

    return (
        <div className='container'>
            <div className='row'>
                <div className='col'>
                <h1 className="text-x1 mb-4">Bienvenido {user.email}</h1>
                    <h1>MIS DATOS</h1>
                    <form onSubmit={update}>
                        <div className='mb-3'>
                            <label className='form-label'>Description</label>
                            <input
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                                type="text"
                                className='form-control'
                            />    
                        </div>
                        <div className='mb-3'>
                            <label className='form-label'>Stock</label>
                            <input
                                value={stock}
                                onChange={(e) => setStock(e.target.value)}
                                type="text"
                                className='form-control'
                            />    
                        </div>
                        <button type='submit' className='btn btn-primary'>update</button>
                    </form>
                </div>
            </div>
        </div>
    )
    }
