import { useAuth } from "../context/AuthContext";
import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import {collection, getDocs, getDoc, deleteDoc, doc, updateDoc} from 'firebase/firestore'
import { db } from "../firebase";

export function Home(){
    const {user, logout, loading} = useAuth();
    /*const [usuario, setUsuario] = useState({
        email: '',
        password: '',
        name: '',
        birthDate: '',
        LastName: '',
        DNI: '',
        key: '',
        zone: '',
        riskFactor: '',
        doseAmountCovid: '',
        hasVaccineFlu: '',
        vaccinationDateFlu: '',
        hasYellowFever: '',
        doseYearYellowFever: ''
      });
    const handleChange = ({target: {name, value}}) => {
        setUsuario({...user, [name]: value});
    };

    const handleLogout = async () => {
        await logout();
    }
    const update = async (e) => { //e es un evento
        e.preventDefault() //para evitar comportamiento por defecto
        //const product= doc(db, "products", id) //traemos todos los datos a product
        //const data= {description: description, stock: stock} //obtenemos data
        const userRef = doc(db,`Persona/${user.email}`)
        //const snapshot = await getDoc(userRef)
        const data= {email: email, stock: stock}
        //console.log(snapshot.data().user.clave)
        await updateDoc(userRef, data) //la data son los datos actualizados, updateDoc es de firestore, para actualizar los datos
    }
    */
        
    
    if(loading) return <h1>loading</h1>

    return  <div className="w-full max-w-xs m-auto text-black">
                <div className="bg-white rounded shadow-md px-8 pt-6 pb-8 mb-4">
                    <h1 className="text-x1 mb-4">Bienvenido {user.email}</h1>
                    <form onSubmit={update}>
                    <div className='mb-3'>
                        <label className='form-label'>Description</label>
                        <input
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            type="text"
                            className='form-control'
                        />    
                    </div>
                    <div className='mb-3'>
                        <label className='form-label'>Stock</label>
                        <input
                            value={stock}
                            onChange={(e) => setStock(e.target.value)}
                            type="text"
                            className='form-control'
                        />    
                    </div>
                    <button type='submit' className='btn btn-primary'>update</button>
                </form>

                    <button className="bg-slate-200 hover:bg-slate-300 rounded py-2 px-4 text-black" onClick={handleLogout}>logout</button>
                </div>
            </div>;
}
