                 } else {
                   setPage((currPage) => currPage + 1);
                 }
               }}
             >
               {page === FormTitles.length - 1 ? "Registrate" : "Próxima hoja"}
             </button>
           </div>
         </div>
        </form>
    </div>
  );
}
