import { useAuth } from "../context/AuthContext";
import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import {collection, getDocs, getDoc, deleteDoc, doc} from 'firebase/firestore'
import { db } from "../firebase";

export function Home(){
    const {user, logout, loading} = useAuth();
    const [datos, setDatos] = useState( [] )

    const handleLogout = async () => {
        await logout();
    }
    const getDatos = async () => {
        const userRef = doc(db,`Persona/${user.email}`)
        const snapshot = await getDoc(userRef)
    }
    
    if(loading) return <h1>loading</h1>

    return  <div className="w-full max-w-xs m-auto text-black">
                <div className="bg-white rounded shadow-md px-8 pt-6 pb-8 mb-4">
                    <h1 className="text-x1 mb-4">Bienvenido {user.email}</h1>
                    <table className='table table-dark table-hover'>
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Stock</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {datos.map( (product) => (
                            <tr key={product.id}>
                                <td>{product.description}</td>
                                <td>{product.stock}</td>
                                <td>
                                    <Link to={`/edit/${product.id}`} className="btn btn-light"><i className="fa-solid fa-pen"></i></Link>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                    <button className="bg-slate-200 hover:bg-slate-300 rounded py-2 px-4 text-black" onClick={handleLogout}>logout</button>
                </div>
            </div>;
}
