import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from 'react-router-dom';
import { createUserDocument } from "../firebase";
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'
import { Alert } from "./Alert";
const MySwal = withReactContent(Swal);
export function Cargar(){
    const {user, setUser, loading} = useAuth();
    const [error, setError] = useState();
    const navigate = useNavigate()
    const handleChange = ({target: {name, value}}) => {
        setUser({...user, [name]: value});
    };  
    if(loading) return <h1>loading</h1>
    function calculoDeEdad(BirthDate) {
        let hoy = new Date();
        console.log(hoy)
        let cumpleanios = new Date(BirthDate);
        console.log(BirthDate)
        let edad = hoy.getFullYear() - cumpleanios.getFullYear();
        let m = hoy.getMonth() - cumpleanios.getMonth();
        if (m < 0 || (m === 0 && hoy.getDate() < cumpleanios.getDate())) { //aca no seria m==0 en vez de m===0 ???
            edad--;
        }
        console.log(edad);
        return edad;
      }
      function calculoGripe(vaccinationDateFlu){ //esta funcion es igual a la de calculo de edad pero weno
        let hoy = new Date()
        let ultimaVacGripe = new Date(vaccinationDateFlu)
        let años= hoy.getFullYear() - ultimaVacGripe.getFullYear();
        let m = hoy.getMonth() - ultimaVacGripe.getMonth();
        if (m < 0 || (m === 0 && hoy.getDate() < ultimaVacGripe.getDate())) { //aca no seria m==0 en vez de m===0 ???
            años--;
        }
        return años;
      }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        try {
            await createUserDocument(user)
            MySwal.fire(`Su codigo de validación es: ${user.key}, por favor, anotelo`)
            let boolCovid = false; 
            let boolFlu = false; 
            if((calculoDeEdad(user.birthDate) > 60)){ 
                if((user.doseAmountCovid < 2)){ 
                    boolCovid = true; //AUTOMATICO, DE RIESGO = MAYOR DE 60, CON MENOS DE 2 DOSIS
                } 
            }else{ 
                if(user.riskFactor === "true"){
                    if(user.doseAmountCovid < 2){
                        boolCovid = true; //AUTOMATICO, DE RIESGO = MENOR DE 60, PERO CON FACTORES DE RIESGO Y MENOS DE 2 DOSIS
                    }  
                }else{
                    if(user.doseAmountCovid < 2){
                        boolCovid = true; //MANUAL, NO ES DE RIESGO = MENOR DE 60, MENOS DE 2 DOSIS
                    }
                }
            }   
            //VACUNA GRIPE 
            if(user.hasVaccineFlu === "false"){ //MIRAR CASO DE SI TIENE +60 AÑOS, SIN VACUNA, Y CON VACUNA VENCIDA, ESTO EN FIREBASE.JS
                boolFlu = true; //AUTOMATICO, NO TIENE VACUNA GRIPE
            }else{
                if(calculoGripe(user.vaccinationDateFlu)){ 
                    boolFlu = true; //AUTOMATICO, TIENE VACUNA GRIPE VENCIDA
                }
            }
            //AVISOS 
            if(boolCovid && boolFlu){ 
                alert("Se le asigno una vacuna para el covid y para la gripe"); 
            }else if(boolCovid){ 
                alert("Se le asigno una vacuna para el covid"); 
            }else if(boolFlu){ 
                alert("Se le asigno una vacuna para la gripe"); 
            } 
            navigate('/');
        } catch (error) {
            setError(error.message);
        }
    };
    return (
        <div className='container'>
            <form onSubmit={handleSubmit}>
                <div className='row'>
                    <div className='col'>
                        <div className='mb-3'>
                            <label className='form-label' htmlFor="zone">Vacunatorio de preferencia</label>
                            <br></br>
                            <input type="radio" name="zone" className='form-control' value={"Municipalidad"} onChange={handleChange} required/> Municipalidad (51 e/10 y 11 Nro. 770)
                            <br></br>
                            <input type="radio" name="zone" className='form-control' value={"Terminal"} onChange={handleChange} required/> Terminal (3 e/ 41 y 42 Nro. 480)
                            <br></br>
                            <input type="radio" name="zone" className='form-control' value={"Cementerio"} onChange={handleChange} required/> Cementerio (138 e/73 y 74 Nro. 2035)    
                        </div>
                        <div className='mb-3'>
                            <label className='form-label' htmlFor="riskFactor">¿Sos una persona con factores de riesgo?</label>
                            <input type="radio" name="riskFactor" className='form-control' value={true} onChange={handleChange} required/> Si
                            <input type="radio" name="riskFactor" className='form-control' value={false} onChange={handleChange} required/> No 
                        </div>
                        <h1> Datos Vacuna COVID-19 </h1>
                        <div className='mb-3'>
                            <label className='form-label' htmlFor="doseAmountCovid">Cantidad de dosis de COVID-19 recibidas</label>
                            <input type="number" name="doseAmountCovid" className='form-control' onChange={handleChange} required min={0} max={2}/>    
                        </div>
                        <h1> Datos Vacuna GRIPE </h1>
                        <div className='mb-3'>
                            <label className='form-label' htmlFor="hasVaccineFlu">¿Posee la vacuna para la gripe?</label>
                            <input type="radio" name="hasVaccineFlu" className='form-control' value={true} onChange={handleChange} required/> Si
                            <input type="radio" name="hasVaccineFlu" className='form-control' value={false} onChange={handleChange} required/> No
                        </div>
                        { (user.hasVaccineFlu === "true") ?
                        <div className='mb-3'>
                            <label className='form-label' htmlFor="vaccinationDateFlu">¿En qué fecha se la dió?</label>
                            <input type="date" name="vaccinationDateFlu" className='form-control' onChange={handleChange} required />    
                        </div>
                        : ""} 
                        <h1> Datos Vacuna FIEBRE AMARILLA </h1>
                        <div className='mb-3'>
                            <label className='form-label' htmlFor="hasYellowFever">¿Tiene la vacuna de la fiebre amarilla?</label>
                            <input type="radio" name="hasYellowFever" className='form-control' value={true} onChange={handleChange} required/> Si 
                            <input type="radio" name="hasYellowFever" className='form-control' value={false} onChange={handleChange} required/> No
                        </div>
                        { (user.hasYellowFever === "true") ?
                        <div className='mb-3'>
                            <label className='form-label' htmlFor="doseYearYellowFever">Año en que se la aplicó</label>
                            <input type="number" name="doseYearYellowFever" className='form-control' onChange={handleChange} min={1900} max={2022} required/>    
                        </div>
                        : ""}
                    </div>
                    <button onClick={handleSubmit}>Cargar datos</button>
                </div>
            </form>
      </div>
    )
}
