import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export function Register(){
    const [user, setUser] = useState({
        email: '',
        password: '',
    });

    const {signup}= useAuth();

    const navigate = useNavigate();

    const [error, setError] = useState();

    const handleChange = ({target: {name, value}}) => {
        setUser({...user, [name]: value});
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        try {
            await signup(user.email, user.password);
            navigate('/');
        } catch (error) {
            if(error.code === "auth/weak-password"){
                setError("Contrasenia debil, deberia tener al menos 6 caracteres")
            }
            console.log(error.code);
            if(error.code === 'auth/email-already-in-use'){
                setError("El email ya existe")
            }
            setError(error.message);
        }

    };

    return (
        <div>
            {error && <p>{error}</p>}
            <form onSubmit={handleSubmit}>
                <label htmlFor="email">email</label>
                <input type="email" name="email" placeholder="yourEmail@company" onChange={handleChange}/>
                <label htmlFor="password">email</label>
                <input type="password" name="password" placeholder="******" id="password" onChange={handleChange}/>
                <button>Registrar</button>
            </form>
        </div>
    );
}
