import { useAuth } from "../context/AuthContext";
import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import {collection, getDocs, getDoc, deleteDoc, doc, updateDoc} from 'firebase/firestore'
import { db } from "../firebase";
import { info } from "autoprefixer";

export function Home(){
    const {user, logout, loading} = useAuth();
    
    const [usuario, setUsuario] = useState( " " );

    const handleChange = ({target: {name, value}}) => {
        setUsuario({...user, [name]: value});
    };

    const handleLogout = async () => {
        await logout();
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        const userRef = doc(db,`Persona/${user.email}`)
        const snapshot = await getDoc(userRef)
        const info= snapshot.data().user
    }
    //const mostrarDatos = async () => { //e es un evento
        //e.preventDefault() //para evitar comportamiento por defecto
        //const product= doc(db, "Persona", user.email) //traemos todos los datos a product
        //const data= {description: description, stock: stock} //obtenemos data
       // const userRef = doc(db,`Persona/${user.email}`)
       // const snapshot = await getDoc(userRef)
        //console.log(snapshot.data().user.clave)
        //await updateDoc(userRef, data) //la data son los datos actualizados, updateDoc es de firestore, para actualizar los datos
    //}
    //useEffect(()=>{
    //    mostrarDatos();
    //})
        
    
    if(loading) return <h1>loading</h1>

    return  <div className="w-full max-w-xs m-auto text-black">
                <div className="bg-white rounded shadow-md px-8 pt-6 pb-8 mb-4">
                    <h1 className="text-x1 mb-4">Bienvenido {user.email}</h1>
                <h1> TUS DATOS</h1>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label htmlFor="email" className="block text-gray-700 text-sm font-fold mb-2">email</label>
                        <input type="email" name="email" placeholder={user.email} className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"  disabled />
                    </div>
                    <div className="mb-4">
                        <label htmlFor="name" className="block text-gray-700 text-sm font-fold mb-2">nombre</label>
                        <input type="text" name="name" placeholder={user.name} className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" onChange={handleChange} disabled />
                    </div>
                    <div className="mb-4">
                        <label htmlFor="LastName" className="block text-gray-700 text-sm font-fold mb-2">apellido</label>
                        <input type="text" name="LastName" placeholder="Perez" className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" onChange={handleChange} disabled />
                    </div>
                    <div className="mb-4">
                        <label htmlFor="birthDate" className="block text-gray-700 text-sm font-fold mb-2">fecha de nacimiento</label>
                        <input type="date" name="birthDate" placeholder="01/01/1986" className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" onChange={handleChange} disabled />
                    </div>
                    <div className="mb-4">
                        <label htmlFor="DNI" className="block text-gray-700 text-sm font-fold mb-2">dni</label>
                        <input type="number" name="DNI" placeholder="12345678" className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" onChange={handleChange} disabled />
                    </div>
                    <div className='mb-3'>
                        <label className='form-label' htmlFor="zone">Vacunatorio de preferencia</label>
                        <input type="radio" name="zone" className='form-control' value={"Municipalidad"} onChange={handleChange} /> Municipalidad
                        <input type="radio" name="zone" className='form-control' value={"Terminal"} onChange={handleChange} /> Terminal
                        <input type="radio" name="zone" className='form-control' value={"Cementerio"} onChange={handleChange} /> Cementerio     
                    </div>
                    <div className='mb-3'>
                          <label className='form-label' htmlFor="riskFactor">¿Sos una persona con factores de riesgo?</label>
                          <input type="radio" name="riskFactor" className='form-control' value={true} onChange={handleChange} /> Si
                          <input type="radio" name="riskFactor" className='form-control' value={false} onChange={handleChange} /> No 
                      </div>
                      <h1> Datos Vacuna COVID-19 </h1>
                      <div className='mb-3'>
                          <label className='form-label' htmlFor="doseAmountCovid">Cantidad de dosis de COVID-19 recibidas</label>
                          <input type="number" name="doseAmountCovid" className='form-control' onChange={handleChange}  min={0} max={2}/>    
                      </div>
                      <h1> Datos Vacuna GRIPE </h1>
                      <div className='mb-3'>
                          <label className='form-label' htmlFor="hasVaccineFlu">¿Posee la vacuna para la gripe?</label>
                          <input type="radio" name="hasVaccineFlu" className='form-control' value={true} onChange={handleChange} /> Si
                          <input type="radio" name="hasVaccineFlu" className='form-control' value={false} onChange={handleChange} /> No
                      </div>
                      <div className='mb-3'>
                          <label className='form-label' htmlFor="vaccinationDateFlu">¿En qué fecha se la dió?</label>
                          <input type="date" name="vaccinationDateFlu" className='form-control' onChange={handleChange}  />    
                      </div> 
                      <h1> Datos Vacuna FIEBRE AMARILLA </h1>
                      <div className='mb-3'>
                          <label className='form-label' htmlFor="hasYellowFever">¿Tiene la vacuna de la fiebre amarilla?</label>
                          <input type="radio" name="hasYellowFever" className='form-control' value={true} onChange={handleChange} /> Si 
                          <input type="radio" name="hasYellowFever" className='form-control' value={false} onChange={handleChange} /> No
                      </div>
                      <div className='mb-3'>
                          <label className='form-label' htmlFor="doseYearYellowFever">Año en que se la aplicó</label>
                          <input type="number" name="doseYearYellowFever" className='form-control' onChange={handleChange}  min={1900} max={2022}/>    
                      </div>
                      <button className="bg-slate-200 hover:bg-slate-300 rounded py-2 px-4 text-black" onClick={handleChange}>actualizar datos</button>
                </form>
                    <button className="bg-slate-200 hover:bg-slate-300 rounded py-2 px-4 text-black" onClick={handleLogout}>cerrar sesión</button>
                </div>
            </div>;
}
